import './App.css';
import React, {useState} from "react"
import TodoItem from './components/TodoItem'; // import TodoItem

const App = () => {
  const [todos, setTodos] = useState([
    { text: "Buy Food" },
    { text: "Learn React" },
    { text: "Build todo app" }
  ]);

  return (
    <div className="app">
      <div className="todo-list">
        {todos.map(todo => (
          <TodoItem todo={todo} />  // Panggil TodoItem 
        ))}
      </div>
    </div>
  );
}

export default App;
