import React from 'react'
import TodoItem from './TodoItem'

const TodoList = ({todos}) => {
    return (
        <div className="todo-list">
        {todos.map((todo, i) => (
          <TodoItem key={i} todo={todo} idx={i} />     // tambahkan props idx.
        ))}
      </div>
    )
}

export default TodoList
